SELECT primkey, ( CASE WHEN PetalWidthCm IS NULL THEN 'Iris-setosa' WHEN PetalWidthCm<=0.8 THEN 'Iris-setosa' WHEN PetalWidthCm IS NULL THEN 'Iris-setosa' WHEN PetalWidthCm>0.8 THEN CASE WHEN PetalWidthCm IS NULL THEN 'Iris-versicolor' WHEN PetalWidthCm<=1.75 THEN CASE WHEN PetalLengthCm IS NULL THEN 'Iris-versicolor' WHEN PetalLengthCm<=5.35 THEN 'Iris-versicolor' WHEN PetalLengthCm IS NULL THEN 'Iris-versicolor' WHEN PetalLengthCm>5.35 THEN 'Iris-virginica' ELSE NULL END WHEN PetalWidthCm IS NULL THEN 'Iris-versicolor' WHEN PetalWidthCm>1.75 THEN 'Iris-virginica' ELSE NULL END ELSE NULL END) AS Species FROM (SELECT primkey, SepalLengthCm AS SepalLengthCm, SepalWidthCm AS SepalWidthCm, PetalLengthCm AS PetalLengthCm, PetalWidthCm AS PetalWidthCm FROM input AS ms_input) AS model_input